mapper.select:
SELECT e.ex_uid AS exUid, e.ex_title AS exTitle, e.ex_content AS exContent, e.ex_level AS exLevel, e.view_count AS viewCount, l.ex_lang_name AS exLangName FROM exam e LEFT JOIN exam_language l ON e.ex_lang_uid = l.ex_lang_uid
AND (l.ex_lang_name LIKE CONCAT('%', #{lang}, '%') OR #{lang} LIKE CONCAT('%', l.ex_lang_name, '%'))
ORDER BY e.ex_uid DESC LIMIT #{size} OFFSET #{offset}

mapper.update:
        UPDATE exam
		SET view_count = view_count + 1
		WHERE ex_uid = #{ex_uid}
