mapper.select:
SELECT COUNT(*) 
FROM member 
WHERE username = #{username}

mapper.update:
UPDATE member
SET mb_password = #{mb_password}
WHERE mb_uid = #{mb_uid}

mapper.select:
SELECT mb_password
FROM member
WHERE mb_uid = #{mb_uid}

SELECT COUNT(*)
FROM member
WHERE mb_nickname = #{nickname}
	AND mb_uid != #{mb_uid}
