mapper.select:
SELECT COUNT(*)
FROM member
WHERE username = #{username}

SELECT COUNT(*)
FROM member
WHERE username = #{username}
AND
mb_pnumber = #{mb_pnumber}

mapper.update:
UPDATE member
SET
mb_password = #{mb_password}
WHERE username = #{username}

mapper.select:
SELECT mb_password
FROM member
WHERE mb_uid = #{mb_uid}

mapper.update:
UPDATE member
SET mb_password = #{mb_password}
WHERE
mb_uid = #{mb_uid}

mapper.select:
SELECT COUNT(*)
FROM member
WHERE mb_nickname = #{nickname}
AND mb_uid != #{mb_uid}
